private void RemoverFila(OrdenesDetalle detalle)
{
    try
    {
        var producto_ = ProductosBLL.Buscar(productos.ProductoId);
        producto_.ValorInventario += ordenesDetalle.Cantidad;
        ordenes.OrdenesDetalle.Remove(detalle);
        CalcularTotal();
    }
    catch (Exception)
    {
        throw;
    }
}


    

